def constructible_values_in_S(N: int):
# 1. Build S = { floor(i^2 / N) : i = 1..N }
S = { (i * i) // N for i in range(1, N + 1) }
# 2. Precompute all numbers ≤ max(S) that are of the form a^2 + b^2
max_S = max(S)
limit = int(math.isqrt(max_S))
sums_of_two_squares = set()
for a in range(limit + 1):
a2 = a * a
for b in range(limit + 1):
v = a2 + b * b
if v > max_S:
break
sums_of_two_squares.add(v)
# 3. Intersect with S.
constructible_in_S = (S & sums_of_two_squares) - {0}
return S, constructible_in_S
if __name__ == "__main__":
N = 2015
S, constructible_in_S = constructible_values_in_S(N)
print("N =", N)
print("|S| =", len(S))
print("Number of constructible elements in S (v = a^2 + b^2, v > 0):",
len(constructible_in_S))
# If you want to see them:
print(sorted(constructible_in_S))
\end{verbatim}

