from itertools import permutations
adjacency = {
0: [1, 5],
1: [0, 2, 6],
2: [1, 3, 7],
3: [2, 4, 8],
4: [3, 9],
5: [0, 6],
6: [5, 7, 1],
7: [6, 8, 2],
8: [7, 9, 3],
9: [8, 4]
}
def get_group(person):
return person // 3
def is_valid(arrangement):
for seat, person in enumerate(arrangement):
if person == -1:
continue
person_group = get_group(person)
for neighbor_seat in adjacency[seat]:
neighbor = arrangement[neighbor_seat]
if neighbor != -1 and get_group(neighbor) == person_group:
return False
return True
total_count = 0
for empty_seat in range(10):
people = list(range(9))
seats = [i for i in range(10) if i != empty_seat]
for perm in permutations(people):
arrangement = [-1] * 10
for i, person in enumerate(perm):
arrangement[seats[i]] = person
if is_valid(arrangement):
total_count += 1
print(f"Answer: {total_count}")
\end{verbatim}

