#!/usr/bin/env python3
"""
학생들의 서로 피하고 싶은 상대 정보(충돌 그래프)를 바탕으로
필요한 최소 학급 수(그래프 색칠 수)를 계산한다.
표 형태("학생 & 금지1, 금지2")나 JSON(dict) 파일을 입력으로 받을 수 있으며,
CLI 없이도 import 해서 `assign_classes` 함수를 직접 호출해 사용할 수 있다.
"""
from __future__ import annotations
import argparse
import json
from dataclasses import dataclass
from pathlib import Path
from typing import (
Dict,
FrozenSet,
Iterable,
Iterator,
List,
Mapping,
MutableMapping,
Sequence,
Set,
Tuple,
)
@dataclass(frozen=True)
class ClassAssignment:
"""색칠(학급 배정) 결과."""
class_count: int
classes: List[List[str]]
assignment: Dict[str, int]
def normalize_identifier(value: object) -> str:
"""학생 ID를 문자열로 통일."""
return str(value).strip()
def normalize_conflicts(
conflicts: Mapping[object, Sequence[object]],
extra_students: Iterable[object] | None = None,
) -> Dict[str, Set[str]]:
"""충돌 정보를 무방향 그래프로 정규화."""
graph: Dict[str, Set[str]] = {}
if extra_students:
for student in extra_students:
graph.setdefault(normalize_identifier(student), set())
for student, blockers in conflicts.items():
student_id = normalize_identifier(student)
graph.setdefault(student_id, set())
for other in blockers or []:
other_id = normalize_identifier(other)
if other_id == student_id:
continue
graph.setdefault(other_id, set())
graph[student_id].add(other_id)
graph[other_id].add(student_id)
return graph
def order_vertices(graph: Mapping[str, Set[str]]) -> List[str]:
"""백트래킹 효율을 높이기 위한 정점 순서(높은 차수 우선)."""
return sorted(
graph.keys(),
key=lambda node: (len(graph[node]), node),
reverse=True,
)
def build_class_lists(assignment: Mapping[str, int]) -> List[List[str]]:
"""색칠 정보를 학급별 학생 목록으로 변환."""
grouped: Dict[int, List[str]] = {}
for student, color in assignment.items():
grouped.setdefault(color, []).append(student)
classes = []
for color in sorted(grouped):
classes.append(sorted(grouped[color]))
return classes
def canonicalize_partition(
assignment: Mapping[str, int],
) -> FrozenSet[FrozenSet[str]]:
"""학급 배치를 색 이름에 상관없는 불변 집합으로 변환."""
classes = build_class_lists(assignment)
return frozenset(frozenset(group) for group in classes)
def assign_classes(
conflicts: Mapping[object, Sequence[object]],
extra_students: Iterable[object] | None = None,
) -> ClassAssignment:
"""주어진 충돌 그래프의 최소 학급 배치를 계산."""
graph = normalize_conflicts(conflicts, extra_students)
if not graph:
return ClassAssignment(0, [], {})
vertex_order = order_vertices(graph)
assignment: Dict[str, int] = {}
color_usage: MutableMapping[int, int] = {}
best_assignment: Dict[str, int] | None = None
best_colors = len(graph) + 1  # 상한
def dfs(index: int) -> None:
nonlocal best_assignment, best_colors
if index == len(vertex_order):
colors_used = len(color_usage)
if colors_used < best_colors:
best_colors = colors_used
best_assignment = assignment.copy()
return
current = vertex_order[index]
forbidden = {assignment[nb] for nb in graph[current] if nb in assignment}
# 이미 사용 중인 색상 먼저 시도
for color in sorted(color_usage.keys()):
if color in forbidden:
continue
assignment[current] = color
color_usage[color] += 1
dfs(index + 1)
color_usage[color] -= 1
if color_usage[color] == 0:
del color_usage[color]
del assignment[current]
# 새로운 색상 시도 (가지치기)
new_color = len(color_usage) + 1
if new_color >= best_colors or new_color in forbidden:
return
assignment[current] = new_color
color_usage[new_color] = 1
dfs(index + 1)
del assignment[current]
del color_usage[new_color]
dfs(0)
assert best_assignment is not None  # 그래프가 비어있지 않으므로 항상 존재
classes = build_class_lists(best_assignment)
return ClassAssignment(best_colors, classes, best_assignment)
def enumerate_class_partitions(
conflicts: Mapping[object, Sequence[object]],
class_count: int,
extra_students: Iterable[object] | None = None,
) -> Iterator[FrozenSet[FrozenSet[str]]]:
"""정확히 class_count개의 학급으로 나누는 모든 경우의 수를 생성."""
if class_count <= 0:
raise ValueError("class_count는 1 이상이어야 합니다.")
graph = normalize_conflicts(conflicts, extra_students)
if not graph:
return iter(())
vertex_order = order_vertices(graph)
assignment: Dict[str, int] = {}
def dfs(index: int, colors_used: int) -> Iterator[FrozenSet[FrozenSet[str]]]:
if index == len(vertex_order):
if colors_used == class_count:
yield canonicalize_partition(assignment)
return
current = vertex_order[index]
forbidden = {assignment[nb] for nb in graph[current] if nb in assignment}
for color in range(min(colors_used, class_count)):
if color in forbidden:
continue
assignment[current] = color
yield from dfs(index + 1, colors_used)
del assignment[current]
if colors_used < class_count and colors_used not in forbidden:
assignment[current] = colors_used
yield from dfs(index + 1, colors_used + 1)
del assignment[current]
return dfs(0, 0)
def collect_partitions(
conflicts: Mapping[object, Sequence[object]],
class_count: int,
extra_students: Iterable[object] | None = None,
count_only: bool = False,
max_list: int = 20,
) -> Tuple[int, List[FrozenSet[FrozenSet[str]]]]:
"""분할 수를 계산하고 일부 예시를 저장."""
partitions_seen: Set[FrozenSet[FrozenSet[str]]] = set()
samples: List[FrozenSet[FrozenSet[str]]] = []
max_examples = max(0, max_list or 0)
total = 0
for partition in enumerate_class_partitions(
conflicts, class_count, extra_students
):
if partition in partitions_seen:
continue
partitions_seen.add(partition)
total += 1
if not count_only and len(samples) < max_examples:
samples.append(partition)
return total, samples
def format_partition(part: FrozenSet[FrozenSet[str]]) -> str:
"""불변 집합 형태의 분할을 사람이 읽기 쉽게 문자열화."""
groups = sorted(
(sorted(group) for group in part),
key=lambda g: (len(g), g),
)
formatted = ["{" + ", ".join(group) + "}" for group in groups]
return " | ".join(formatted)
def parse_table_lines(lines: Iterable[str]) -> Dict[str, List[str]]:
"""
"학생 & 금지1, 금지2" 형태 텍스트를 파싱.
비어 있는 줄과 # 주석은 무시한다.
"""
conflicts: Dict[str, List[str]] = {}
for raw_line in lines:
line = raw_line.strip()
if not line or line.startswith("#"):
continue
if "&" not in line:
raise ValueError(f'"&" 구분자가 없는 줄: {line}')
student_part, others_part = line.split("&", 1)
student = normalize_identifier(student_part)
others = [
normalize_identifier(token)
for token in others_part.split(",")
if normalize_identifier(token)
]
conflicts[student] = others
return conflicts
def load_conflicts(path: Path) -> Dict[str, List[str]]:
"""파일 확장자에 따라 JSON 혹은 텍스트로 파싱."""
text = path.read_text(encoding="utf-8")
if path.suffix.lower() == ".json":
data = json.loads(text or "{}")
if not isinstance(data, dict):
raise ValueError("JSON 입력은 객체(dict)여야 합니다.")
normalized: Dict[str, List[str]] = {}
for student, others in data.items():
if others is None:
normalized[normalize_identifier(student)] = []
elif isinstance(others, (list, tuple, set)):
normalized[normalize_identifier(student)] = [
normalize_identifier(o) for o in others
]
else:
raise ValueError(f"{student}: 리스트가 아닌 값 {others!r}")
return normalized
return parse_table_lines(text.splitlines())
def parse_args() -> argparse.Namespace:
parser = argparse.ArgumentParser(
description="학생 충돌 정보를 기반으로 최소 학급 수를 계산합니다.",
)
parser.add_argument(
"-i",
"--input",
type=Path,
help="충돌 정보 파일 경로 (JSON 또는 '학생 & 금지1, 금지2' 텍스트)",
)
parser.add_argument(
"-s",
"--student",
dest="students",
action="append",
help="충돌 리스트에는 없지만 포함해야 하는 학생 (여러 번 사용 가능)",
)
parser.add_argument(
"-c",
"--target-classes",
type=int,
help="정확히 N개 학급으로 나누는 경우의 수를 계산",
)
parser.add_argument(
"--count-only",
action="store_true",
help="경우의 수 개수만 출력 (target-classes 사용 시)",
)
parser.add_argument(
"--max-list",
type=int,
default=20,
help="count-only가 아닐 때 표시할 분할 예시 개수",
)
return parser.parse_args()
def main() -> None:
args = parse_args()
if args.input:
conflicts = load_conflicts(args.input)
else:
# 예시 데이터 (문제에서 제공된 표)
conflicts = {
"1": ["2", "3", "4"],
"2": ["4", "7"],
"3": ["5", "4"],
"4": ["1", "7"],
"5": ["3"],
}
target = args.target_classes
students = args.students
def print_partitions_summary(
class_count: int, total: int, samples: List[FrozenSet[FrozenSet[str]]]
) -> None:
print(f"{class_count}개 학급으로 나눌 수 있는 상이한 경우의 수: {total}")
if args.count_only:
return
for idx, partition in enumerate(samples, start=1):
print(f"- 경우 {idx}: {format_partition(partition)}")
if total > len(samples):
remaining = total - len(samples)
print(f"(그 외 {remaining}개의 경우는 생략)")
if target:
total, samples = collect_partitions(
conflicts,
target,
students,
args.count_only,
args.max_list,
)
print_partitions_summary(target, total, samples)
return
result = assign_classes(conflicts, students)
print(f"필요한 최소 학급 수: {result.class_count}")
for idx, members in enumerate(result.classes, start=1):
print(f"학급 {idx}: {', '.join(members)}")
if result.class_count == 0:
return
total, samples = collect_partitions(
conflicts,
result.class_count,
students,
args.count_only,
args.max_list,
)
print_partitions_summary(result.class_count, total, samples)
if __name__ == "__main__":
main()
\end{verbatim}
