F := GF(3);
G := Matrix(F, [
[1, 0, 0, 0, 1, 2, 2],
[0, 1, 0, 0, 1, 1, 1],
[0, 0, 1, 0, 2, 1, 2],
[0, 0, 0, 1, 2, 0, 1]
]);
// ---- 일반 함수 정의 ----
// 주어진 생성행렬 G에 대해, G와 동치인 코드를 생성하는
// 모든 생성행렬의 개수를 구하는 함수
function NumberOfEquivalentGeneratorMatrices(G)
// field, parameters
F := BaseRing(G);
q := #F;
k := Nrows(G);
n := Ncols(G);
// GL_k(F_q)의 크기: ∏_{i=0}^{k-1} (q^k - q^i)
sizeGL := 1;
for i in [0..k-1] do
sizeGL *:= q^k - q^i;
end for;
// Monomial group of length n: (q-1)^n * n!
sizeMonomial := (q - 1)^n * Factorial(n);
// 코드와 automorphism group
C := LinearCode(G);
AutC := AutomorphismGroup(C);
sizeAut := #AutC;
// 동치인 모든 generator matrix의 개수
numGen := sizeGL * sizeMonomial div sizeAut;
return numGen, sizeGL, sizeMonomial, sizeAut;
end function;
// ---- 실제 계산 ----
numGen, sizeGL, sizeMonomial, sizeAut := NumberOfEquivalentGeneratorMatrices(G);
print "q =", #BaseRing(G);
print "k =", Nrows(G), "n =", Ncols(G);
print "|GL_k(F_q)|           =", sizeGL;
print "|Monomial(n,F_q)|     =", sizeMonomial;
print "|Aut(C)|              =", sizeAut;
print "# equivalent G's      =", numGen;
\end{verbatim}
