\begin{lstlisting}[language=Python, caption={Counting voting outcomes for $n$ voters}]
def count_outcomes(n):
max_vote = 2 * n
total = 0
for a in range(max_vote + 1):
for b in range(max_vote + 1):
for c in range(max_vote + 1):
S = a + b + c
if S < n or S > 2 * n:
continue
t = S - n
need = max(0, t - c) + max(0, t - b) + max(0, t - a)
if need <= t:
total += 1
return total
print(count_outcomes(400))
\end{lstlisting}
