{

\begin{minted}[fontsize=\footnotesize, linenos, tabsize=4, breaklines]{python}
from typing import Iterable, Optional
import random
def count_perfect_mappings(n: int) -> int:
"""
Return the number of perfect mappings for A={1,...,n}:
N = sum_{t=1}^n t^n
"""
if n <= 0:
raise ValueError("n must be a positive integer.")
return sum(t**n for t in range(1, n+1))
class PerfectMapping:
"""
A concrete perfect mapping f: 2^A -> A for A={1,...,n}.
Construction (as proven):
- Choose t in {1,...,n}
- Choose b_i in {1,...,t} independently for i=1..n
Then define
f(X) = t                     if X = A
f(X) = min_{i not in X} b_i  otherwise
Representation of subsets X accepted by f():
- Iterable of ints from 1..n (e.g., {1,3,5})
- OR an integer bitmask: bit i-1 corresponds to element i in A
"""
def __init__(self, n: int, t: int, b: Iterable[int]):
if n <= 0:
raise ValueError("n must be positive.")
if not (1 <= t <= n):
raise ValueError("t must be in {1,...,n}.")
self.n = n
self.t = t
self.b = list(b)
if len(self.b) != n:
raise ValueError(f"b must have length n={n}.")
if any(not (1 <= bi <= t) for bi in self.b):
raise ValueError("All b_i must lie in {1,...,t}.")
def _subset_to_bool_array(self, X) -> list:
"""Normalize input subset X to a boolean membership array of length n."""
inX = [False]*self.n
if isinstance(X, int):
# bitmask: bit i (0-based) corresponds to element i+1 in A
mask = X
for i in range(self.n):
inX[i] = bool((mask >> i) & 1)
else:
# iterable of 1..n
for v in X:
if not (1 <= v <= self.n):
raise ValueError(f"subset elements must be in 1..{self.n}")
inX[v-1] = True
return inX
def f(self, X) -> int:
"""
Evaluate f on subset X (iterable of 1..n or bitmask).
Returns a value in {1,...,n}, as required (indeed in {1,...,t} except when X=A gives t).
"""
inX = self._subset_to_bool_array(X)
# If X == A:
if all(inX):
return self.t
# Otherwise: min over complement indices of b_i
vals = [self.b[i] for i in range(self.n) if not inX[i]]
return min(vals)
def __repr__(self):
return f"PerfectMapping(n={self.n}, t={self.t}, b={self.b})"
def sample_perfect_mapping(n: int, rng: Optional[random.Random] = None) -> PerfectMapping:
"""
Sample a random perfect mapping by choosing:
t ~ Uniform{1,...,n}
b_i ~ Uniform{1,...,t} independently
"""
rng = rng or random
t = rng.randint(1, n)
b = [rng.randint(1, t) for _ in range(n)]
return PerfectMapping(n, t, b)
# -----------------------------
# Demo / quick self-check
# -----------------------------
if __name__ == "__main__":
n = 7
print(f"n={n}: number of perfect mappings = {count_perfect_mappings(n)}")
\end{minted}
